package Javaprogram;

public class Multipletable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int table = 9;

        // Manually print each line without any loops
        System.out.printf("%d × 1 = %d%n" + table * 1);
        System.out.printf("%d × 2 = %d%n" + table * 2);
        System.out.printf("%d × 3 = %d%n" + table * 3);
        System.out.printf("%d × 4 = %d%n"+ table * 4);
        System.out.printf("%d × 5 = %d%n" + table * 5);
        System.out.printf("%d × 6 = %d%n" + table * 6);
        System.out.printf("%d × 7 = %d%n" + table * 7);
        System.out.printf("%d × 8 = %d%n"+ table * 8);
        System.out.printf("%d × 9 = %d%n" + table * 9);
        System.out.printf("%d × 10 = %d%n"+ table * 10);
		
	}

}
